import { Component, OnInit } from '@angular/core';
import { Dog } from '../model/dog';
import { DogService } from '../service/dogservice';
import { Router } from '@angular/router';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  dogs: Dog[] = [];
  dog: Dog = new Dog();

  constructor(private dogService: DogService, private router: Router) { }

  ngOnInit(): void {
    this.dogService.getDogs().subscribe((data: Dog[]) => {
      this.dogs = data;
    });
  }


}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Dashboard</title>

    <!-- Add Bootstrap CSS -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/css/bootstrap.min.css" rel="stylesheet">
    <link rel="stylesheet" href="dashboard.component.css">
</head>
<body>

    <!-- Navigation Bar -->
    <nav class="navbar navbar-expand-lg navbar-light bg-light fixed-top">
        <a class="navbar-brand" href="#">
            <img src="/assets/logo.svg" height="50px" alt="Your Logo">
        </a>

        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
                <li class="nav-item active">
                    <a class="nav-link" href="/dashboard">Home</a>
                </li>
                <li class="nav-item">
                    <a class= "nav-link" href="/aboutus">About</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="#">Services</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="/contact">Contact</a>
                </li>
            </ul>
        </div>
        <a href="/add-dog" class="btn btn-success">Add a dog</a>
    </nav>

    <!-- Page Content -->
    <div class="container mt-5 pt-5"> 
        <div class="row" id="dog-cards">
            <!-- Dog cards will be populated here using JavaScript -->
        </div>
    </div>

    <footer class="bg-info text-center text-lg-start fixed-bottom" style="background-color: #006400 !important; color: white;">
        <!-- Copyright -->
        <div class="text-center p-3">
            &copy; 2023 Dog Adopt
        </div>
        <!-- Copyright -->
    </footer>

    <!-- Include Bootstrap JS (optional) -->
    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
    <script>
        $(document).ready(function() {
            $.get("/api/dogs", function(data) {
                data.forEach(function(dog) {
                    var cardHtml = `
                        <div class="col-md-3">
                            <div class="card rounded-card shadow">
                                <img src="${dog.photo}" class="card-img-top img-fluid rounded-square" alt="${dog.name}">
                                <div class="card-body d-flex flex-column align-items-center justify-content-center">
                                    <h5 class="card-title">${dog.name}</h5>
                                    <p class="card-text">${dog.breed}</p>
                                    <a href="/api/show-dog/${dog.id}" class="btn btn-success">Get to know me</a>
                                </div>
                            </div>
                        </div>
                    `;
                    $('#dog-cards').append(cardHtml);
                });
            });
        });
    </script>
</body>
</html>
