import { Component } from '@angular/core';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css']
})
export class IndexComponent {

}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Welcome Page</title>

    <!-- Add Bootstrap CSS -->
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/css/bootstrap.min.css" rel="stylesheet">

    <!-- Custom CSS to set the background image and center the content -->
    <style>
        body {
            background-image: url('/assets/dog2.jpg'); 
            background-size: cover;
            background-position: center;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
        }

      /* 
        .welcome-container {
            text-align: center;
            background: rgba(255, 255, 255, 0.8); /* semi-transparent white background for better text readability 
            padding: 20px;
            border-radius: 10px;
        }
        */

        .btn-primary {
            background-color: #007BFF;
            border-color: #007BFF;
        }
    </style>
</head>
<body>
    <div class="welcome-container">
        <h1 class="welcome-text">Welcome to Dog Adopt!</h1>
        <p class="welcome-text">Adopt, don't shop. Find your new best friend:</p>
        <a href="/dashboard" class="btn btn-primary">Get Started</a>
    </div>

    <!-- Add Bootstrap JS (optional) -->
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@4.5.2/dist/js/bootstrap.min.js"></script>
</body>
</html>
